mapper.delete:
delete
from class_file
where class_id = #{classId}
  and file_id = #{fileId}

mapper.select:
select f.name, f.extension, f.size, f.updated_time as updatedTime, f.created_time as createTime, f.url from class c left join class_file cf on c.id = cf.class_id left join file f on cf.file_id = f.id where c.id = #{classId}
f.name like '%${param}%'
order by c.id desc
